class Write{constructor(){this.interval,this.elements,this.elementsIndex=0,this.index=0}selectElements(){if(this.elements||(this.elements=document.querySelectorAll("*[data-writter]")),this.elementsIndex==this.elements.length)this.elements[this.elementsIndex-1].classList.add("write");else{let e=this.elements[this.elementsIndex];this.addLetter(e,e.getAttribute("data-writter")),e.classList.toggle("write")}}addLetter(e,t){let s=this;e.innerHTML=t.slice(0,this.index),this.index++,s.index>t.length?(s.index=0,clearInterval(s.interval),s.elementsIndex++,s.selectElements(),e.classList.toggle("write")):(window.clearInterval(s.interval),s.interval=setInterval(()=>{s.addLetter(e,t)},s.randomSpeed()))}randomSpeed(e=100,t=300){return Math.floor(Math.random()*(t-e)+e)}}